INSERT INTO `forum_posts` VALUES(460, 1335, '2007-10-12 15:56:33', 're:Weighted Dice.', 442, 55, '2007-10-12 15:56:33', 'This system is going to have a one week grace period from a time that we deem it ok to instill it. That will give everyone enough time to read it, comprehend it, and post how they want their pool handled. Seven days from when we post it, it becomes set in stone and we can''t say or do anything to change it. \r\nAll current DKP is to be wiped and we all start over on an even footing.', 'no', '', 0);
